import styled from 'styled-components';
import 'animate.css';
import { Jumbotron } from './App.styled';

export const QuoteContainer = styled.div`
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
position: absolute;
left: 0;
height: 100%;
width: 200px;
background-color: rgba(238, 236, 237, 0.8);
flex-grow: 6;
overflow: hidden;
animation: slideInLeft 1s;
& label {
color: #020106;
font-family: 'text';
font-size: 14px;
font-weight: medium;
}
& h1 {
font-family: 'heading';
font-size: 24px;
color: #020106;
}
& input[type="text"] {
width: 85%;
height: 36px;
font-family: 'text';
font-size: 18px;
font-weight: medium;
}
`;

export const QuoteButton = styled.button`
display: flex;
justify-content: center;
align-items: center;
width: 32px;
height: 32px;
border-radius: 4px;
border: none;
outline: none;
background: transparent;
& img {
	width: 32px;
	height: 32px;
filter: invert(9%) sepia(44%) saturate(2701%) hue-rotate(235deg) brightness(87%) contrast(107%);


}
&:hover {
	animation: rubberBand .5s;
}
`;

export const Header = styled(Jumbotron)`
z-index: 0;
width: 200px;
height: 15vh;
opacity: 0.8;
& h1 {
    font-size: 18px;
    color: #EEECED;
    opacity: 1
}
animation: slideInLeft 1s;
`;


export const MessageBox = styled.header`
display: flex;
justify-content: center;
align-items: center;
width: 95%;
height: 200px;
background-color: Gray;
border: 1px inset Gray;
border-radius: 4px;
& p {
	color: #0D0D0D;
	font-family: 'text';
	font-size: 16px;
    font-weight: medium;
}
`;;
