import React, { useState } from 'react';
import { QuoteContainer, QuoteButton } from '../styles/QuoteBar.styled';
import share from './share.png';

const QuoteBar = () => {
    const [songNum, setSongNum] = useState(null);

    function setSongs(e) {
        setSongNum(e.target.value);
    }

    return (
        <QuoteContainer>
        <h1>Mixing Quote Calculator:</h1>
        <label>How Many Songs?</label>
        <br />
        <input type="text" onChange={setSongs} value={songNum} />
        <QuoteButton><img src={share} alt="share" /></QuoteButton>
</QuoteContainer>
    );
}

export default QuoteBar;
