import React, { useState } from 'react';
import { DashboardContainer, SuccessMessage, FailMessage, Uploader } from '../styles/Dashboard.styled';
import axios from 'axios';
import upload from './Upload.png';
import quote from './Quote.png';
import QuoteBar from './quotebar.js';
import Loader from './loader';
import { Jumbotron, Text } from '../styles/App.styled';

const Dashboard = () => {
	const [worked, setWorked] = useState(false);
	const [getQuote, setGetQuote] = useState(false);
	const [print, setPrint] = useState('');
	const [name, setName] = useState('');
	const [showMessage, setShowMessage] = useState(false);
	const[loading, setLoading] = useState(false);

        function onFileUpload(e) {
		setLoading(true);
		let formData = new FormData();
		formData.append("mySong",
			e.target.files[0]);
		axios.post("/uploadsong", formData, {
			headers: {
		"current-user-name": name
			},
		}).then(({data}) => {
			setLoading(false);
			setPrint(data.message);
			setShowMessage(true);
			data.successfulUpload ? setWorked(true) : setWorked(false);
		})
	}

	function setUser(e) {
		setName(e.target.value);
    }

        function showQuote() {
		setGetQuote(!getQuote);
	}

	return (
        <>
        <Jumbotron>
        <h1>Dashboard</h1>
        </Jumbotron>
        		<DashboardContainer>
        <Text>
		<label>Enter your name:</label>
		<input type="text" onChange={setUser} value={name}/> <br />
        </Text>
		{loading ? <Loader /> : <div />}
		<Uploader>
		Upload a file:
		<input type="file" onChange={onFileUpload} />
		<img src={upload} alt="upload" />
		</Uploader> <br />
		<Uploader>
		Get a mixing quote:
		<img src={quote} alt="button" onClick={showQuote} />
		</Uploader>
		{showMessage ?
			<>
			{worked ? 
			<SuccessMessage>
			<p>{print}</p> 
			</SuccessMessage> :
			<FailMessage>
			<p>{print}</p>
			</FailMessage>}
			</> :
			<div />
		} <br />

		{getQuote ? <QuoteBar close={showQuote}/> : <div />}
		</DashboardContainer>
      </>
	);
}

export default Dashboard;
